mapper.select:
SELECT w.wgt_cd, w.car_cd, c.car_num, w.fst_wgt_dt, w.fst_wgh, w.wgt_num, w.cst_cd, cs.cst_nm, w.prdt_cd, p.prdt_nm, w.scnd_wgt_dt, w.scnd_wgh, w.dscnt_bss_cd, w.dscnt_val, w.dscnt, w.rl_wgh, w.io_flg, (SELECT io.cd_nm FROM cm_cd io WHERE w.io_flg = io.cd_val AND io.cd_id = 'IO_FLG') AS io_flg_nm, w.nt, w.wgt_flg_cd, w.wgt_stat_cd, w.del_yn, w.unt_prc, w.amt, w.trn_dt, w.rgn_dt, w.rgn_id, w.edt_dt, w.edt_id , w.drvr, w.ext_1, w.ext_2, w.ext_3, w.ext_4, w.ext_5, w.ext_6 , (SELECT usr_nm FROM cm_usr_inf u WHERE u.lgn_id = w.edt_id) edt_nm , p.prdt_mgt_cd, cs.cst_mgt_cd, c.car_mgt_cd FROM ts_wgt_inf w LEFT OUTER JOIN ts_cst_mst cs ON (cs.cst_cd = w.cst_cd) LEFT OUTER JOIN ts_prdt_mst p ON (p.prdt_cd = w.prdt_cd), ts_car_mst c WHERE w.car_cd = c.car_cd AND w.del_yn = 'N'
AND w.wgt_cd = #{wgt_cd}
AND w.wgt_stat_cd = #{wgt_stat_cd} AND w.fst_wgt_dt >= DATE_ADD(CURRENT_DATE, -30)
AND w.wgt_stat_cd = #{wgt_stat_cd} AND w.scnd_wgt_dt >= CURRENT_DATE
AND w.wgt_flg_cd = #{wgt_flg_cd}
AND w.car_cd = #{car_cd}
AND c.car_num LIKE '%'||#{car_num}||'%'
AND w.cst_cd = #{cst_cd}
AND cs.cst_nm LIKE '%'||#{cst_nm}||'%'
AND w.prdt_cd = #{prdt_cd}
AND p.prdt_nm LIKE '%'||#{prdt_nm}||'%'
AND w.scnd_wgt_dt BETWEEN CAST(#{ext_5} AS DATE) AND CAST(#{ext_6} AS TIMESTAMP)
AND w.io_flg = #{io_flg}
